import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "./Createpost.css";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const Createpost = () => {
  const [body, setBody] = useState("");
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const notifyError = (msg) => toast.error(msg);
  const notifySuccess = (msg) => toast.success(msg);

  // Upload image to Cloudinary
  const uploadImage = async () => {
    if (!image) {
      notifyError("Please select an image");
      return null;
    }

    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "insta_cloud"); // ðŸ”¹ change to your upload_preset
    data.append("cloud_name", "cloudchandana");   // ðŸ”¹ change to your cloud_name

    try {
      const res = await fetch(
        "https://api.cloudinary.com/v1_1/cloudchandana/image/upload",
        {
          method: "POST",
          body: data,
        }
      );

      const result = await res.json();

      if (!result.url) {
        notifyError("Cloudinary upload failed");
        return null;
      }
      return result.url;
    } catch (err) {
      console.error("Cloudinary upload error:", err);
      notifyError("Cloudinary upload error");
      return null;
    }
  };

  // Post creation
  const postDetails = async () => {
    setLoading(true);
    const imageUrl = await uploadImage();
    if (!imageUrl) {
      setLoading(false);
      return;
    }

    try {
      const res = await fetch(`${API_URL}/posts/createpost`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + localStorage.getItem("jwt"),
        },
        body: JSON.stringify({
          body,
          pic: imageUrl, // ðŸ”¹ send the uploaded Cloudinary URL
        }),
      });

      const data = await res.json(); // ðŸ”¹ safe JSON parse
      if (data.error) {
        notifyError(data.error);
      } else {
        notifySuccess("Successfully posted");
        navigate("/"); // redirect to home
      }
    } catch (err) {
      console.error("Backend post creation error:", err);
      notifyError("Post creation failed");
    } finally {
      setLoading(false);
    }
  };

  // Preview selected image
  const loadFile = (event) => {
    const output = document.getElementById("output");
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function () {
      URL.revokeObjectURL(output.src);
    };
    setImage(event.target.files[0]);
  };

  return (
    <div className="createpost">
      <div className="post-header">
        <h4 style={{ margin: "3px auto" }}>Create New Post</h4>
        <button
          id="post-btn"
          onClick={postDetails}
          disabled={loading} // ðŸ”¹ disable while uploading
        >
          {loading ? "Posting..." : "Share"}
        </button>
      </div>

      <div className="main-div">
        <img
          id="output"
          src="via.placeholder.com/â€¦.png?text=Preview:1"
          alt="preview"
        />
        <input type="file" accept="image/*" onChange={loadFile} />
      </div>

      <div className="details">
        <div className="card-header">
          <div className="card-pic">
            <img
              src="https://t4.ftcdn.net/jpg/03/83/25/83/240_F_383258331_D8imaEMl8Q3lf7EKU2Pi78Cn0R7KkW9o.jpg"
              alt=""
            />
          </div>
          <h5>minakshi</h5>
        </div>
        <textarea
          value={body}
          onChange={(e) => setBody(e.target.value)}
          type="text"
          placeholder="Write a caption..."
        ></textarea>
      </div>
    </div>
  );
};

export default Createpost;
