import React, { useEffect, useState } from 'react'
import './Home.css';
import {  toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
const Home = () => {
  var profileLink="https://cdn-icons-png.flaticon.com/128/64/64572.png"
  const navigate = useNavigate();
  const [data, setData] = useState([])
  const [comment, setComment] = useState(" ")
  const [show,setShow] =useState(false);
  const [item, setItem] = useState([])
  const [posts, setPosts] = useState()
  const notifya=(msg)=>toast.error(msg)
  const notifyb=(msg)=>toast.success(msg)


  const toggleComment=(posts)=>{
    if(show){
      setShow(false);
     
    }else{
      setShow(true);
      setItem(posts)
     
    }
  }
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("./signup")
    }
  
  // const toggleComment=(posts)=>{
  //   if(show){
  //     setShow(false);
     
  //   }else{
  //     setShow(true);
  //     setItem(posts)
     
  //   }
  // }
    //  fetching all post
    fetch("/allposts", {
      header: {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },

    }).then(res => res.json())
    .then((result)=>{
      console.log(result)
      setData(result)
    })
      .catch(err => console.log(err))
  }, [])

  const likepost = (id) => {
    fetch("/like", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body: JSON.stringify({
        postId: id
      })
    }).then(res => res.json())
      .then((result) => {
        const newdata = data.map((posts) => {
          if (posts._id == result._id) {
            return result
          } else {
            return posts
          }
        })
        setData(newdata)
        console.log(result)
      })
  }

  const unlikepost = (id) => {
    fetch("/unlike", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body: JSON.stringify({
        postId: id
      })
    }).then(res => res.json())
      .then((result) => {
        const newdata = data.map((posts) => {
          if (posts._id == result._id) {
            return result
          } else {
            return posts
          }
        })
        setData(newdata)
        console.log(result)
      })
  }

  const makeComment = (text, id) => {
    // console.log(comment)
    fetch("/comment", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body: JSON.stringify({
        postId: id,
        text: text,
      })
    }).then(res => res.json())
      .then((result) => {
         const newdata = data.map((posts) => {
          if (posts._id == result._id) {
            return result
          } else {
            return posts
          }
        })
        setData(newdata)
        setComment(" ")
        notifyb("suuccesfully commented")
        console.log(result)
      })
  }
  return (
    <div className="home">
      {data.map((posts) => {
        // console.log(posts)
        return (
          <div className="card">
            <div className="card-header">
              <div className="card-pic">
                <img src={posts.postedby.photo ?posts.postedby.photo:"profileLink"} alt=" " />
                <h5>
                  <Link to={`/profile/${posts.postedby._id}`}>
                  {posts.postedBy.name}
                  </Link>
                  </h5>
              </div>
            </div>
            <div className="card-image">
              <img src={posts.photo} alt="" />
            </div>
            {
              posts.likes.includes(JSON.parse(localStorage.getItem("user"))._id)
                ? (
                  <span className="material-symbols-outlined material-symbols-outlined -red" onClick={() => { unlikepost(posts._id) }}>
                    favorite
                  </span>
                ) : (<span className="material-symbols-outlined" onClick={() => { likepost(posts._id) }}>
                  favorite
                </span>)
            }
            <div className="card-content">
              <p > {item.likes.length} like</p>
              <p>{posts.body}</p>
            </div>

            <div className="add-comment">
              <span className="material-symbols-outlined">
                mood
              </span>
              <input type="text" placeholder='Add a Comment' value={comment} onChange={(e) => { setComment(e.target.value) }} />
              <button className="comment" onClick={() => { makeComment(comment, posts._id) }}>Post</button>
            </div>
          </div>
        )
})}

      {show && (
        <div className="showcomment">
        <div className="container">
          <div className="postpic">
            <img src="" alt="" />
          </div>
          <div className="details">

            {/* card-header */}
            <div className="card-header" style={{ borderBottom: "1px solid #00000029" }}>
              <div className="card-pic">
                <img src={item.photo} alt=" " />
                <h5>{item.postedby.name}</h5>
              </div>
            </div>

            {/* comment-section */}
            <div className="commentsection" style={{ borderBottom: "1px solid #00000029" }} >
              {
                item.comments.map((comment)=>{
                return  (<p className='comm'>
                <span className="commenter" style={{ fontWeight: "bolder" }} >{comment.postedby}{" "}  </span>
                <span className="commenttext">{comment.comment}</span>
              </p>)
                })
              }
              
            </div>

            {/* card content */}
            <div className="card-content">
              <p >{posts.likes.length} likes</p>
              <p>{posts.body}</p>
              <p style={{fontWeight:"bold",cyrsor:"pointer"}}
              onClick={()=>{
                toggleComment(posts);
              }}>
                View all comments
              </p>
            </div>
            <div className="add-comment">
              <span className="material-symbols-outlined">
                mood
              </span>
              <input type="text" placeholder='Add a Comment' value={comment} onChange={(e) => { setComment(e.target.value) }} />
              <button className="comment"
               onClick={()=>{makeComment(comment,item._id)
                toggleComment();
               }}
              >Post</button>
            </div>
          </div>
        </div>
        <div className="closecomment" onClick={()=>{toggleComment(posts)}}>
          <span className="material-symbols-outlined material-symbols-outlined-comment">
            close
          </span>
        </div>
      </div>
      )}
    </div>
  )
}


export default Home
