import React,{useState,useEffect} from 'react'
import './Createpost.css';
import {  toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const Createpost = () => {
 const [body, setBody] = useState("");
 const [image, setImage] = useState("");
 const [url, setUrl] = useState("");
 const navigate=useNavigate()

  const notifya=(msg)=>toast.error(msg)
  const notifyb=(msg)=>toast.success(msg)
 useEffect(()=>{
  if(url){
    fetch(`${API_URL}/posts/createpost`,
      {
        method:"POST",
        headers:{
            "Content-Type":"application/json",
            "Authorization":"Bearer "+localStorage.getItem("jwt")
        },
        body:JSON.stringify({
          body,
          pic:url
        })
  })
  // .then(res=>res.json())
  // .then(data=>{
  //   if(data.error){
  //   notifya(data.error)
  // }else{
  //   notifyb("succesfully posted ")
  //   navigate("/")
  // }




  .then(res => {
  console.log(res.status, res.headers.get("content-type"));
  return res.text(); // temporarily read as text
})
.then(text => {
  console.log(text); 
})
  .catch(err=> console.log(err))
  }

 },[url])
  //posting image to cloudinary

  const postDetails = ()=>{
     if (!body || !image) {
      notifya("Please add all fields");
      return;
    }
    
    const data=new FormData();
    data.append("file",image)
    data.append("upload_preset","insta_cloud")
    data.append("cloud_name","cloudchandana")
    fetch("https://api.cloudinary.com/v1_1/cloudchandana/image/upload",{
      method:"POST",
      body:data
    }).then( res => res.json())
    .then ( data => {
    if (!data.url) {
      notifya("Cloudinary upload failed");
      return;
    }
  
    setUrl(data.url);
  })
  .catch(err=>console.log(err))
}
  
    const loadFile=(event)=>{ 
    var output = document.getElementById('output');
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function() {
      URL.revokeObjectURL(output.src) ;
     }
    setImage(event.target.files[0]);
  };
  return (
    <div className='createpost'>
      <div className="post-header">
         <h4 style={{margin:"3px auto"}}>create new post</h4>
        <button id="post-btn" onClick={()=>{postDetails()}}>Share</button>
       </div>
      <div className="main-div">
        <img id='output' src="data:image/png;base64,"/>
        <input type="file" accept="image/*" onChange={(event)=>{loadFile(event); setImage(event.target.files[0])}}/>
      </div>
      <div className="details">
        <div className="card-header">
            <div className="card-pic">
                <img src="https://t4.ftcdn.net/jpg/03/83/25/83/240_F_383258331_D8imaEMl8Q3lf7EKU2Pi78Cn0R7KkW9o.jpg" alt="" />
            </div>
            <h5>minakshi</h5>
        </div>
        <textarea value={body} onChange={(e)=>{setBody(e.target.value)}} type="text" placeholder='write a caption....' ></textarea>
      </div>
    </div>
  )
}

export default Createpost
